    "function_name": "create_message_notifications",
    "function_owner": "postgres",
    "function_definition": "CREATE OR REPLACE FUNCTION public.create_message_notifications()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nBEGIN\r\n    -- Create notifications for all participants except the sender\r\n    INSERT INTO chat_notifications (user_id, room_id, message_id, type)\r\n    SELECT \r\n        cp.user_id,\r\n        NEW.room_id,\r\n        NEW.id,\r\n        'message'\r\n    FROM chat_participants cp\r\n    WHERE cp.room_id = NEW.room_id \r\n    AND cp.user_id != NEW.sender_id\r\n    AND cp.is_active = true;\r\n    \r\n    RETURN NEW;\r\nEND;\r\n$function$\n"
  },
  {
    "trigger_schema": "public",
    "table_name": "chat_participants",
    "trigger_name": "chat_participant_count_trigger",
    "trigger_definition": "CREATE TRIGGER chat_participant_count_trigger AFTER INSERT OR DELETE OR UPDATE ON chat_participants FOR EACH ROW EXECUTE FUNCTION update_chat_room_participant_count()",
