    "function_oid": 117515,
    "function_name": "update_chat_room_participant_count",
    "function_owner": "postgres",
    "function_definition": "CREATE OR REPLACE FUNCTION public.update_chat_room_participant_count()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nBEGIN\r\n    IF TG_OP = 'INSERT' THEN\r\n        UPDATE chat_rooms \r\n        SET participant_count = (\r\n            SELECT COUNT(*) \r\n            FROM chat_participants \r\n            WHERE room_id = NEW.room_id AND is_active = true\r\n        )\r\n        WHERE id = NEW.room_id;\r\n        RETURN NEW;\r\n    ELSIF TG_OP = 'UPDATE' THEN\r\n        UPDATE chat_rooms \r\n        SET participant_count = (\r\n            SELECT COUNT(*) \r\n            FROM chat_participants \r\n            WHERE room_id = NEW.room_id AND is_active = true\r\n        )\r\n        WHERE id = NEW.room_id;\r\n        RETURN NEW;\r\n    ELSIF TG_OP = 'DELETE' THEN\r\n        UPDATE chat_rooms \r\n        SET participant_count = (\r\n            SELECT COUNT(*) \r\n            FROM chat_participants \r\n            WHERE room_id = OLD.room_id AND is_active = true\r\n        )\r\n        WHERE id = OLD.room_id;\r\n        RETURN OLD;\r\n    END IF;\r\n    RETURN NULL;\r\nEND;\r\n$function$\n"
  },
