    "trigger_name": "calculate_cost_price_trigger",
    "trigger_definition": "CREATE TRIGGER calculate_cost_price_trigger BEFORE INSERT OR UPDATE ON custom_products FOR EACH ROW EXECUTE FUNCTION calculate_custom_product_cost_price()",
    "enabled_flag": "O",
    "function_oid": 123992,
    "function_name": "calculate_custom_product_cost_price",
    "function_owner": "postgres",
    "function_definition": "CREATE OR REPLACE FUNCTION public.calculate_custom_product_cost_price()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nBEGIN\r\n    -- If cost_price is null but price (MRP) is set, calculate cost_price\r\n    -- Using a default margin of 30% (cost = 70% of MRP)\r\n    IF NEW.cost_price IS NULL AND NEW.price IS NOT NULL AND NEW.price > 0 THEN\r\n        NEW.cost_price := ROUND(NEW.price * 0.70, 2);  -- 30% margin\r\n    END IF;\r\n    \r\n    RETURN NEW;\r\nEND;\r\n$function$\n"
  },
