  {
    "trigger_schema": "public",
    "table_name": "deliveries",
    "trigger_name": "trigger_update_route_efficiency",
    "trigger_definition": "CREATE TRIGGER trigger_update_route_efficiency AFTER INSERT OR DELETE OR UPDATE ON deliveries FOR EACH ROW EXECUTE FUNCTION update_route_efficiency()",
    "enabled_flag": "O",
    "function_oid": 87298,
    "function_name": "update_route_efficiency",
    "function_owner": "postgres",
    "function_definition": "CREATE OR REPLACE FUNCTION public.update_route_efficiency()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nBEGIN\r\n    -- Update route statistics and efficiency\r\n    UPDATE delivery_routes \r\n    SET \r\n        route_efficiency_score = (\r\n            SELECT \r\n                CASE \r\n                    WHEN total_deliveries = 0 THEN 0\r\n                    ELSE ROUND(\r\n                        (completed_deliveries::numeric / total_deliveries::numeric) * 100 * \r\n                        (1 - (COALESCE(route_distance, 0) / NULLIF(COALESCE(route_distance, 0) + 100, 0))), 2\r\n                    )\r\n                END\r\n        ),\r\n        current_load_items = (\r\n            SELECT COALESCE(SUM(di.quantity_to_deliver), 0)\r\n            FROM deliveries d\r\n            JOIN delivery_items di ON d.id = di.delivery_id\r\n            WHERE d.route_id = COALESCE(NEW.route_id, OLD.route_id) \r\n            AND di.item_status IN ('loaded', 'in_transit')\r\n        ),\r\n        updated_at = now()\r\n    WHERE id = COALESCE(NEW.route_id, OLD.route_id);\r\n    \r\n    RETURN COALESCE(NEW, OLD);\r\nEND;\r\n$function$\n"
  },
