    "trigger_definition": "CREATE TRIGGER trg_general_ledger_update_balance AFTER INSERT OR DELETE OR UPDATE ON general_ledger FOR EACH ROW EXECUTE FUNCTION trigger_update_account_balance()",
    "enabled_flag": "O",
    "function_oid": 131990,
    "function_name": "trigger_update_account_balance",
    "function_owner": "postgres",
    "function_definition": "CREATE OR REPLACE FUNCTION public.trigger_update_account_balance()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nDECLARE\r\n    affected_account_id UUID;\r\nBEGIN\r\n    -- Handle different trigger events\r\n    IF TG_OP = 'INSERT' OR TG_OP = 'UPDATE' THEN\r\n        affected_account_id := NEW.account_id;\r\n        RETURN NEW;\r\n    ELSIF TG_OP = 'DELETE' THEN\r\n        affected_account_id := OLD.account_id;\r\n        RETURN OLD;\r\n    END IF;\r\n    \r\n    -- Update account balance\r\n    UPDATE chart_of_accounts \r\n    SET \r\n        current_balance = calculate_account_balance(affected_account_id),\r\n        updated_at = CURRENT_TIMESTAMP\r\n    WHERE id = affected_account_id;\r\n    \r\n    RETURN COALESCE(NEW, OLD);\r\nEND;\r\n$function$\n"
  },
  {
    "trigger_schema": "public",
    "table_name": "general_ledger",
    "trigger_name": "trg_general_ledger_validate_account",
    "trigger_definition": "CREATE TRIGGER trg_general_ledger_validate_account BEFORE INSERT OR UPDATE ON general_ledger FOR EACH ROW EXECUTE FUNCTION trigger_validate_account_usage()",
    "enabled_flag": "O",
    "function_oid": 132001,
    "function_name": "trigger_validate_account_usage",
    "function_owner": "postgres",
    "function_definition": "CREATE OR REPLACE FUNCTION public.trigger_validate_account_usage()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nDECLARE\r\n    account_info RECORD;\r\nBEGIN\r\n    -- Get account information\r\n    SELECT account_type, is_active INTO account_info\r\n    FROM chart_of_accounts\r\n    WHERE id = NEW.account_id;\r\n    \r\n    -- Validate account is active\r\n    IF NOT account_info.is_active THEN\r\n        RAISE EXCEPTION 'Cannot post to inactive account';\r\n    END IF;\r\n    \r\n    RETURN NEW;\r\nEND;\r\n$function$\n"
  },
  {
    "trigger_schema": "public",
    "table_name": "general_ledger",
    "trigger_name": "trigger_update_account_balance",
    "trigger_definition": "CREATE TRIGGER trigger_update_account_balance AFTER INSERT OR DELETE ON general_ledger FOR EACH ROW EXECUTE FUNCTION update_account_balance()",
    "enabled_flag": "O",
    "function_oid": 81942,
    "function_name": "update_account_balance",
    "function_owner": "postgres",
    "function_definition": "CREATE OR REPLACE FUNCTION public.update_account_balance()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nBEGIN\r\n  IF TG_OP = 'INSERT' THEN\r\n    UPDATE chart_of_accounts \r\n    SET current_balance = CASE \r\n      WHEN normal_balance = 'DEBIT' \r\n      THEN current_balance + NEW.debit_amount - NEW.credit_amount\r\n      ELSE current_balance + NEW.credit_amount - NEW.debit_amount\r\n    END,\r\n    updated_at = NOW()\r\n    WHERE id = NEW.account_id;\r\n    \r\n    RETURN NEW;\r\n  ELSIF TG_OP = 'DELETE' THEN\r\n    UPDATE chart_of_accounts \r\n    SET current_balance = CASE \r\n      WHEN normal_balance = 'DEBIT' \r\n      THEN current_balance - OLD.debit_amount + OLD.credit_amount\r\n      ELSE current_balance - OLD.credit_amount + OLD.debit_amount\r\n    END,\r\n    updated_at = NOW()\r\n    WHERE id = OLD.account_id;\r\n    \r\n    RETURN OLD;\r\n  END IF;\r\n  \r\n  RETURN NULL;\r\nEND;\r\n$function$\n"
  },
  {
    "trigger_schema": "public",
    "table_name": "invoices",
    "trigger_name": "trg_invoices_create_journal",
    "trigger_definition": "CREATE TRIGGER trg_invoices_create_journal AFTER INSERT ON invoices FOR EACH ROW EXECUTE FUNCTION trigger_create_sales_journal_entry()",
    "enabled_flag": "O",
    "function_oid": 132138,
    "function_name": "trigger_create_sales_journal_entry",
    "function_owner": "postgres",
    "function_definition": "CREATE OR REPLACE FUNCTION public.trigger_create_sales_journal_entry()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nDECLARE\r\n    ar_account_id UUID;\r\n    sales_account_id UUID;\r\n    journal_id UUID;\r\n    customer_name TEXT;\r\n    amount_to_record DECIMAL;\r\nBEGIN\r\n    -- Determine amount to record with better logic\r\n    amount_to_record := COALESCE(NEW.final_price, NEW.total, NEW.total_price, NEW.grand_total, 0);\r\n    \r\n    -- Skip if total is null or zero\r\n    IF amount_to_record IS NULL OR amount_to_record = 0 THEN\r\n        RETURN NEW;\r\n    END IF;\r\n    \r\n    -- Get account IDs with flexible lookup\r\n    SELECT id INTO ar_account_id FROM chart_of_accounts WHERE account_code IN ('1200', '1100') ORDER BY account_code LIMIT 1;\r\n    SELECT id INTO sales_account_id FROM chart_of_accounts WHERE account_code IN ('4010', '4000', '4001') ORDER BY account_code LIMIT 1;\r\n    \r\n    -- Skip if accounts not found but log warning\r\n    IF ar_account_id IS NULL OR sales_account_id IS NULL THEN\r\n        RAISE WARNING 'Required accounts not found for sales journal entry: AR=%, Sales=%. Available codes: %', \r\n            ar_account_id, sales_account_id,\r\n            (SELECT array_agg(account_code) FROM chart_of_accounts WHERE account_code IN ('1200','1100','4010','4000','4001'));\r\n        RETURN NEW;\r\n    END IF;\r\n    \r\n    -- Get customer name\r\n    customer_name := COALESCE(NEW.customer_name, 'Customer');\r\n    \r\n    -- Create journal entry for sale\r\n    journal_id := create_journal_entry(\r\n        CURRENT_DATE,\r\n        'Sale to ' || customer_name,\r\n        CASE \r\n            WHEN TG_TABLE_NAME = 'sales_orders' THEN 'SO-' || NEW.id::text\r\n            WHEN TG_TABLE_NAME = 'invoices' THEN 'INV-' || NEW.id::text\r\n            ELSE 'SALE-' || NEW.id::text\r\n        END\r\n    );\r\n    \r\n    -- Debit Accounts Receivable (increase asset)\r\n    PERFORM add_journal_entry_line(journal_id, ar_account_id, amount_to_record, 0, 'Invoice created');\r\n    \r\n    -- Credit Sales Revenue (increase revenue)\r\n    PERFORM add_journal_entry_line(journal_id, sales_account_id, 0, amount_to_record, 'Sales revenue');\r\n    \r\n    -- Post the journal entry\r\n    PERFORM post_journal_entry(journal_id);\r\n    \r\n    RETURN NEW;\r\nEXCEPTION\r\n    WHEN OTHERS THEN\r\n        -- Log error and continue without failing the sale\r\n        RAISE WARNING 'Failed to create journal entry for sale %: %', NEW.id, SQLERRM;\r\n        RETURN NEW;\r\nEND;\r\n$function$\n"
  },
