  {
    "trigger_schema": "public",
    "table_name": "journal_entries",
    "trigger_name": "trg_journal_entries_validate_balance",
    "trigger_definition": "CREATE TRIGGER trg_journal_entries_validate_balance BEFORE UPDATE ON journal_entries FOR EACH ROW EXECUTE FUNCTION trigger_validate_journal_balance()",
    "enabled_flag": "O",
    "function_oid": 131994,
    "function_name": "trigger_validate_journal_balance",
    "function_owner": "postgres",
    "function_definition": "CREATE OR REPLACE FUNCTION public.trigger_validate_journal_balance()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nBEGIN\r\n    -- Only validate when status changes to POSTED\r\n    IF NEW.status = 'POSTED' AND OLD.status != 'POSTED' THEN\r\n        IF ABS(COALESCE(NEW.total_debit, 0) - COALESCE(NEW.total_credit, 0)) > 0.01 THEN\r\n            RAISE EXCEPTION 'Cannot post unbalanced journal entry. Debits: %, Credits: %', \r\n                NEW.total_debit, NEW.total_credit;\r\n        END IF;\r\n    END IF;\r\n    \r\n    RETURN NEW;\r\nEND;\r\n$function$\n"
  },
  {
    "trigger_schema": "public",
    "table_name": "journal_entries",
    "trigger_name": "trigger_create_gl_entries",
    "trigger_definition": "CREATE TRIGGER trigger_create_gl_entries AFTER UPDATE OF status ON journal_entries FOR EACH ROW EXECUTE FUNCTION create_general_ledger_entries()",
    "enabled_flag": "O",
    "function_oid": 81944,
    "function_name": "create_general_ledger_entries",
    "function_owner": "postgres",
    "function_definition": "CREATE OR REPLACE FUNCTION public.create_general_ledger_entries()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nDECLARE\r\n  line_rec RECORD;\r\n  running_balance NUMERIC(15,2);\r\nBEGIN\r\n  -- Only process when status changes to 'POSTED'\r\n  IF NEW.status = 'POSTED' AND (OLD.status IS NULL OR OLD.status != 'POSTED') THEN\r\n    \r\n    -- Process each journal entry line\r\n    FOR line_rec IN \r\n      SELECT * FROM journal_entry_lines \r\n      WHERE journal_entry_id = NEW.id \r\n      ORDER BY line_number\r\n    LOOP\r\n      -- Calculate running balance for the account\r\n      SELECT COALESCE(\r\n        (SELECT running_balance FROM general_ledger \r\n         WHERE account_id = line_rec.account_id \r\n         ORDER BY created_at DESC, id DESC \r\n         LIMIT 1), \r\n        (SELECT opening_balance FROM chart_of_accounts WHERE id = line_rec.account_id)\r\n      ) INTO running_balance;\r\n      \r\n      -- Update running balance\r\n      running_balance := running_balance + line_rec.debit_amount - line_rec.credit_amount;\r\n      \r\n      -- Insert into general ledger\r\n      INSERT INTO general_ledger (\r\n        account_id,\r\n        journal_entry_id,\r\n        journal_line_id,\r\n        transaction_date,\r\n        posting_date,\r\n        description,\r\n        reference,\r\n        debit_amount,\r\n        credit_amount,\r\n        running_balance,\r\n        source_document_type,\r\n        source_document_id\r\n      ) VALUES (\r\n        line_rec.account_id,\r\n        NEW.id,\r\n        line_rec.id,\r\n        NEW.entry_date,\r\n        NEW.posting_date,\r\n        line_rec.description,\r\n        NEW.reference_number,\r\n        line_rec.debit_amount,\r\n        line_rec.credit_amount,\r\n        running_balance,\r\n        NEW.source_document_type,\r\n        NEW.source_document_id\r\n      );\r\n    END LOOP;\r\n    \r\n    -- Update posting timestamp\r\n    UPDATE journal_entries \r\n    SET posted_at = NOW() \r\n    WHERE id = NEW.id;\r\n  END IF;\r\n  \r\n  RETURN NEW;\r\nEND;\r\n$function$\n"
  },
  {
    "trigger_schema": "public",
