  {
    "trigger_schema": "public",
    "table_name": "journal_entry_lines",
    "trigger_name": "audit_journal_entry_lines",
    "trigger_definition": "CREATE TRIGGER audit_journal_entry_lines AFTER INSERT OR DELETE OR UPDATE ON journal_entry_lines FOR EACH ROW EXECUTE FUNCTION create_audit_trail()",
    "enabled_flag": "O",
    "function_oid": 81946,
    "function_name": "create_audit_trail",
    "function_owner": "postgres",
    "function_definition": "CREATE OR REPLACE FUNCTION public.create_audit_trail()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nDECLARE\r\n  audit_action audit_action;\r\n  old_data JSONB;\r\n  new_data JSONB;\r\nBEGIN\r\n  -- Determine action\r\n  IF TG_OP = 'DELETE' THEN\r\n    audit_action := 'DELETE';\r\n    old_data := row_to_json(OLD)::JSONB;\r\n    new_data := NULL;\r\n  ELSIF TG_OP = 'UPDATE' THEN\r\n    audit_action := 'UPDATE';\r\n    old_data := row_to_json(OLD)::JSONB;\r\n    new_data := row_to_json(NEW)::JSONB;\r\n  ELSIF TG_OP = 'INSERT' THEN\r\n    audit_action := 'CREATE';\r\n    old_data := NULL;\r\n    new_data := row_to_json(NEW)::JSONB;\r\n  END IF;\r\n\r\n  -- Insert audit record\r\n  INSERT INTO audit_trail (\r\n    table_name,\r\n    record_id,\r\n    action,\r\n    old_values,\r\n    new_values,\r\n    user_id,\r\n    timestamp\r\n  ) VALUES (\r\n    TG_TABLE_NAME,\r\n    COALESCE(NEW.id, OLD.id),\r\n    audit_action,\r\n    old_data,\r\n    new_data,\r\n    COALESCE(NEW.created_by, NEW.updated_by, OLD.created_by, OLD.updated_by),\r\n    NOW()\r\n  );\r\n\r\n  RETURN COALESCE(NEW, OLD);\r\nEND;\r\n$function$\n"
  },
  {
    "trigger_schema": "public",
    "table_name": "journal_entry_lines",
    "trigger_name": "trg_journal_lines_update_totals",
    "trigger_definition": "CREATE TRIGGER trg_journal_lines_update_totals AFTER INSERT OR DELETE OR UPDATE ON journal_entry_lines FOR EACH ROW EXECUTE FUNCTION trigger_update_journal_totals()",
    "enabled_flag": "O",
    "function_oid": 131992,
    "function_name": "trigger_update_journal_totals",
    "function_owner": "postgres",
    "function_definition": "CREATE OR REPLACE FUNCTION public.trigger_update_journal_totals()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nDECLARE\r\n    je_id UUID;\r\nBEGIN\r\n    -- Get journal entry ID\r\n    IF TG_OP = 'INSERT' OR TG_OP = 'UPDATE' THEN\r\n        je_id := NEW.journal_entry_id;\r\n    ELSIF TG_OP = 'DELETE' THEN\r\n        je_id := OLD.journal_entry_id;\r\n    END IF;\r\n    \r\n    -- Update journal entry totals\r\n    UPDATE journal_entries \r\n    SET \r\n        total_debit = (\r\n            SELECT COALESCE(SUM(debit_amount), 0)\r\n            FROM journal_entry_lines \r\n            WHERE journal_entry_id = je_id\r\n        ),\r\n        total_credit = (\r\n            SELECT COALESCE(SUM(credit_amount), 0)\r\n            FROM journal_entry_lines \r\n            WHERE journal_entry_id = je_id\r\n        ),\r\n        updated_at = CURRENT_TIMESTAMP\r\n    WHERE id = je_id;\r\n    \r\n    RETURN COALESCE(NEW, OLD);\r\nEND;\r\n$function$\n"
  },
