  {
    "trigger_schema": "public",
    "table_name": "payments",
    "trigger_name": "trg_payments_audit",
    "trigger_definition": "CREATE TRIGGER trg_payments_audit BEFORE INSERT ON payments FOR EACH ROW EXECUTE FUNCTION trigger_audit_financial_transaction()",
    "enabled_flag": "O",
    "function_oid": 132003,
    "function_name": "trigger_audit_financial_transaction",
    "function_owner": "postgres",
    "function_definition": "CREATE OR REPLACE FUNCTION public.trigger_audit_financial_transaction()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nBEGIN\r\n    -- This can be extended to log to an audit table\r\n    -- For now, just ensure created_at is set\r\n    IF TG_OP = 'INSERT' THEN\r\n        IF NEW.created_at IS NULL THEN\r\n            NEW.created_at = CURRENT_TIMESTAMP;\r\n        END IF;\r\n        RETURN NEW;\r\n    END IF;\r\n    \r\n    RETURN NEW;\r\nEND;\r\n$function$\n"
  },
  {
    "trigger_schema": "public",
    "table_name": "payments",
    "trigger_name": "trg_payments_create_journal",
    "trigger_definition": "CREATE TRIGGER trg_payments_create_journal AFTER INSERT ON payments FOR EACH ROW EXECUTE FUNCTION trigger_create_payment_journal_entry()",
    "enabled_flag": "O",
    "function_oid": 132137,
    "function_name": "trigger_create_payment_journal_entry",
    "function_owner": "postgres",
    "function_definition": "CREATE OR REPLACE FUNCTION public.trigger_create_payment_journal_entry()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nDECLARE\r\n    cash_account_id UUID;\r\n    ar_account_id UUID;\r\n    journal_id UUID;\r\n    customer_name TEXT;\r\nBEGIN\r\n    -- Skip if amount is null or zero\r\n    IF NEW.amount IS NULL OR NEW.amount = 0 THEN\r\n        RETURN NEW;\r\n    END IF;\r\n    \r\n    -- Get account IDs with flexible lookup (try multiple account codes)\r\n    SELECT id INTO cash_account_id FROM chart_of_accounts WHERE account_code IN ('1001', '1010') ORDER BY account_code LIMIT 1;\r\n    SELECT id INTO ar_account_id FROM chart_of_accounts WHERE account_code IN ('1200', '1100') ORDER BY account_code LIMIT 1;\r\n    \r\n    -- Skip if accounts not found but log warning\r\n    IF cash_account_id IS NULL OR ar_account_id IS NULL THEN\r\n        RAISE WARNING 'Required accounts not found for payment journal entry: Cash=%, AR=%. Available codes: %', \r\n            cash_account_id, ar_account_id, \r\n            (SELECT array_agg(account_code) FROM chart_of_accounts WHERE account_code IN ('1001','1010','1200','1100'));\r\n        RETURN NEW;\r\n    END IF;\r\n    \r\n    -- Get customer name for description\r\n    IF NEW.sales_order_id IS NOT NULL THEN\r\n        SELECT so.customer_name INTO customer_name \r\n        FROM sales_orders so \r\n        WHERE so.id = NEW.sales_order_id;\r\n    ELSIF NEW.invoice_id IS NOT NULL THEN\r\n        SELECT c.name INTO customer_name FROM customers c \r\n        JOIN invoices i ON c.id = i.customer_id \r\n        WHERE i.id = NEW.invoice_id;\r\n    END IF;\r\n    \r\n    -- Create journal entry for payment\r\n    journal_id := create_journal_entry(\r\n        COALESCE(NEW.payment_date::date, NEW.date::date, CURRENT_DATE),\r\n        'Payment received from ' || COALESCE(customer_name, 'Customer'),\r\n        'PAY-' || NEW.id::text\r\n    );\r\n    \r\n    -- Debit Cash (increase asset)\r\n    PERFORM add_journal_entry_line(journal_id, cash_account_id, NEW.amount, 0, 'Cash received');\r\n    \r\n    -- Credit Accounts Receivable (decrease asset)\r\n    PERFORM add_journal_entry_line(journal_id, ar_account_id, 0, NEW.amount, 'Payment applied to receivable');\r\n    \r\n    -- Post the journal entry\r\n    PERFORM post_journal_entry(journal_id);\r\n    \r\n    RETURN NEW;\r\nEXCEPTION\r\n    WHEN OTHERS THEN\r\n        -- Log error and continue without failing the payment\r\n        RAISE WARNING 'Failed to create journal entry for payment %: %', NEW.id, SQLERRM;\r\n        RETURN NEW;\r\nEND;\r\n$function$\n"
  },
  {
    "trigger_schema": "public",
    "table_name": "payments",
    "trigger_name": "trg_payments_update_invoice_status",
    "trigger_definition": "CREATE TRIGGER trg_payments_update_invoice_status AFTER INSERT OR DELETE OR UPDATE ON payments FOR EACH ROW EXECUTE FUNCTION trigger_update_invoice_payment_status()",
    "enabled_flag": "O",
    "function_oid": 131986,
    "function_name": "trigger_update_invoice_payment_status",
    "function_owner": "postgres",
    "function_definition": "CREATE OR REPLACE FUNCTION public.trigger_update_invoice_payment_status()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nBEGIN\r\n    -- Handle different trigger events\r\n    IF TG_OP = 'INSERT' OR TG_OP = 'UPDATE' THEN\r\n        -- Update for new/updated payment\r\n        IF NEW.invoice_id IS NOT NULL THEN\r\n            PERFORM update_invoice_payment_status(NEW.invoice_id);\r\n        END IF;\r\n        RETURN NEW;\r\n    ELSIF TG_OP = 'DELETE' THEN\r\n        -- Update for deleted payment\r\n        IF OLD.invoice_id IS NOT NULL THEN\r\n            PERFORM update_invoice_payment_status(OLD.invoice_id);\r\n        END IF;\r\n        RETURN OLD;\r\n    END IF;\r\n    \r\n    RETURN NULL;\r\nEND;\r\n$function$\n"
  },
