  {
    "trigger_schema": "public",
    "table_name": "purchase_orders",
    "trigger_name": "trg_purchase_orders_create_journal",
    "trigger_definition": "CREATE TRIGGER trg_purchase_orders_create_journal AFTER INSERT ON purchase_orders FOR EACH ROW EXECUTE FUNCTION trigger_create_purchase_journal_entry()",
    "enabled_flag": "O",
    "function_oid": 132140,
    "function_name": "trigger_create_purchase_journal_entry",
    "function_owner": "postgres",
    "function_definition": "CREATE OR REPLACE FUNCTION public.trigger_create_purchase_journal_entry()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nDECLARE\r\n    inventory_account_id UUID;\r\n    ap_account_id UUID;\r\n    journal_id UUID;\r\n    supplier_name TEXT;\r\nBEGIN\r\n    -- Skip if no total amount\r\n    IF NEW.total IS NULL OR NEW.total = 0 THEN\r\n        RETURN NEW;\r\n    END IF;\r\n    \r\n    -- Get account IDs with flexible lookup\r\n    SELECT id INTO inventory_account_id FROM chart_of_accounts WHERE account_code IN ('1300', '1350', '1320') ORDER BY account_code LIMIT 1;\r\n    SELECT id INTO ap_account_id FROM chart_of_accounts WHERE account_code IN ('2010', '2000') ORDER BY account_code LIMIT 1;\r\n    \r\n    -- Skip if accounts not found but log warning\r\n    IF inventory_account_id IS NULL OR ap_account_id IS NULL THEN\r\n        RAISE WARNING 'Required accounts not found for purchase journal entry: Inventory=%, AP=%. Available codes: %', \r\n            inventory_account_id, ap_account_id,\r\n            (SELECT array_agg(account_code) FROM chart_of_accounts WHERE account_code IN ('1300','1350','1320','2010','2000'));\r\n        RETURN NEW;\r\n    END IF;\r\n    \r\n    -- Get supplier name\r\n    IF NEW.supplier_id IS NOT NULL THEN\r\n        SELECT name INTO supplier_name FROM suppliers WHERE id = NEW.supplier_id;\r\n    END IF;\r\n    \r\n    -- Create journal entry for purchase\r\n    journal_id := create_journal_entry(\r\n        COALESCE(NEW.due_date, CURRENT_DATE),\r\n        'Purchase from ' || COALESCE(supplier_name, 'Supplier'),\r\n        'PO-' || NEW.id::text\r\n    );\r\n    \r\n    -- Debit Inventory (increase asset)\r\n    PERFORM add_journal_entry_line(journal_id, inventory_account_id, NEW.total, 0, 'Inventory purchased');\r\n    \r\n    -- Credit Accounts Payable (increase liability)\r\n    PERFORM add_journal_entry_line(journal_id, ap_account_id, 0, NEW.total, 'Amount owed to supplier');\r\n    \r\n    -- Post the journal entry\r\n    PERFORM post_journal_entry(journal_id);\r\n    \r\n    RETURN NEW;\r\nEXCEPTION\r\n    WHEN OTHERS THEN\r\n        -- Log error and continue without failing the purchase order\r\n        RAISE WARNING 'Failed to create journal entry for purchase order %: %', NEW.id, SQLERRM;\r\n        RETURN NEW;\r\nEND;\r\n$function$\n"
  },
  {
    "trigger_schema": "public",
    "table_name": "sales_order_items",
    "trigger_name": "calculate_sales_order_item_tax_trigger",
    "trigger_definition": "CREATE TRIGGER calculate_sales_order_item_tax_trigger BEFORE INSERT OR UPDATE ON sales_order_items FOR EACH ROW EXECUTE FUNCTION calculate_sales_order_item_tax()",
    "enabled_flag": "O",
    "function_oid": 124085,
    "function_name": "calculate_sales_order_item_tax",
    "function_owner": "postgres",
    "function_definition": "CREATE OR REPLACE FUNCTION public.calculate_sales_order_item_tax()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nDECLARE\r\n    item_tax_percentage DECIMAL(5,2);\r\nBEGIN\r\n    -- Get tax percentage from parent sales order or use item's own tax percentage\r\n    SELECT COALESCE(so.tax_percentage, NEW.tax_percentage, 18.00)\r\n    INTO item_tax_percentage\r\n    FROM sales_orders so\r\n    WHERE so.id = NEW.order_id;\r\n    \r\n    -- Calculate tax for this item\r\n    NEW.tax_percentage := item_tax_percentage;\r\n    NEW.taxable_amount := NEW.quantity * NEW.final_price;\r\n    NEW.tax_amount := ROUND(NEW.taxable_amount * item_tax_percentage / 100, 2);\r\n    \r\n    RETURN NEW;\r\nEND;\r\n$function$\n"
  },
  {
    "trigger_schema": "public",
    "table_name": "sales_order_items",
    "trigger_name": "update_sales_order_item_totals_delete",
    "trigger_definition": "CREATE TRIGGER update_sales_order_item_totals_delete AFTER DELETE ON sales_order_items FOR EACH ROW EXECUTE FUNCTION update_sales_order_item_totals()",
    "enabled_flag": "O",
    "function_oid": 124132,
    "function_name": "update_sales_order_item_totals",
    "function_owner": "postgres",
    "function_definition": "CREATE OR REPLACE FUNCTION public.update_sales_order_item_totals()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nDECLARE\r\n    order_original_price DECIMAL(15,2);\r\n    order_items_subtotal DECIMAL(15,2);\r\n    order_discount_amount DECIMAL(15,2);\r\n    current_freight DECIMAL(15,2);\r\n    current_tax_percentage DECIMAL(5,2);\r\n    current_tax_amount DECIMAL(15,2);\r\n    current_grand_total DECIMAL(15,2);\r\n    current_final_price DECIMAL(15,2);\r\nBEGIN\r\n    -- Get current values to preserve UI calculations\r\n    SELECT \r\n        COALESCE(freight_charges, 0),\r\n        COALESCE(tax_percentage, 18.00),\r\n        COALESCE(tax_amount, 0),\r\n        COALESCE(grand_total, 0),\r\n        COALESCE(final_price, 0)\r\n    INTO \r\n        current_freight,\r\n        current_tax_percentage, \r\n        current_tax_amount,\r\n        current_grand_total,\r\n        current_final_price\r\n    FROM sales_orders \r\n    WHERE id = COALESCE(NEW.order_id, OLD.order_id);\r\n    \r\n    -- Calculate ONLY item-level totals\r\n    SELECT \r\n        COALESCE(SUM(quantity * unit_price), 0),\r\n        COALESCE(SUM(quantity * COALESCE(final_price, unit_price)), 0)\r\n    INTO order_original_price, order_items_subtotal\r\n    FROM sales_order_items \r\n    WHERE order_id = COALESCE(NEW.order_id, OLD.order_id);\r\n    \r\n    order_discount_amount := order_original_price - order_items_subtotal;\r\n    \r\n    -- Update sales_orders - PRESERVE final_price if it was set by UI (includes tax + freight)\r\n    -- Only update final_price if it's currently 0 or matches items subtotal (meaning it wasn't set by UI)\r\n    UPDATE sales_orders SET\r\n        original_price = order_original_price,\r\n        discount_amount = order_discount_amount,\r\n        -- CRITICAL: Only update final_price if it appears to be unset or just item total\r\n        final_price = CASE \r\n            WHEN current_final_price = 0 OR current_final_price = order_items_subtotal THEN \r\n                order_items_subtotal + current_freight + current_tax_amount\r\n            ELSE \r\n                current_final_price  -- PRESERVE UI-calculated final_price\r\n        END,\r\n        -- Always preserve these UI-calculated fields\r\n        freight_charges = current_freight,\r\n        tax_percentage = current_tax_percentage,\r\n        tax_amount = current_tax_amount,\r\n        grand_total = CASE \r\n            WHEN current_grand_total = 0 THEN \r\n                order_items_subtotal + current_freight + current_tax_amount\r\n            ELSE \r\n                current_grand_total  -- PRESERVE UI-calculated grand_total\r\n        END,\r\n        updated_at = NOW()\r\n    WHERE id = COALESCE(NEW.order_id, OLD.order_id);\r\n    \r\n    RETURN COALESCE(NEW, OLD);\r\nEND;\r\n$function$\n"
  },
  {
    "trigger_schema": "public",
    "table_name": "sales_order_items",
    "trigger_name": "update_sales_order_item_totals_insert",
    "trigger_definition": "CREATE TRIGGER update_sales_order_item_totals_insert AFTER INSERT ON sales_order_items FOR EACH ROW EXECUTE FUNCTION update_sales_order_item_totals()",
    "enabled_flag": "O",
    "function_oid": 124132,
    "function_name": "update_sales_order_item_totals",
    "function_owner": "postgres",
    "function_definition": "CREATE OR REPLACE FUNCTION public.update_sales_order_item_totals()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nDECLARE\r\n    order_original_price DECIMAL(15,2);\r\n    order_items_subtotal DECIMAL(15,2);\r\n    order_discount_amount DECIMAL(15,2);\r\n    current_freight DECIMAL(15,2);\r\n    current_tax_percentage DECIMAL(5,2);\r\n    current_tax_amount DECIMAL(15,2);\r\n    current_grand_total DECIMAL(15,2);\r\n    current_final_price DECIMAL(15,2);\r\nBEGIN\r\n    -- Get current values to preserve UI calculations\r\n    SELECT \r\n        COALESCE(freight_charges, 0),\r\n        COALESCE(tax_percentage, 18.00),\r\n        COALESCE(tax_amount, 0),\r\n        COALESCE(grand_total, 0),\r\n        COALESCE(final_price, 0)\r\n    INTO \r\n        current_freight,\r\n        current_tax_percentage, \r\n        current_tax_amount,\r\n        current_grand_total,\r\n        current_final_price\r\n    FROM sales_orders \r\n    WHERE id = COALESCE(NEW.order_id, OLD.order_id);\r\n    \r\n    -- Calculate ONLY item-level totals\r\n    SELECT \r\n        COALESCE(SUM(quantity * unit_price), 0),\r\n        COALESCE(SUM(quantity * COALESCE(final_price, unit_price)), 0)\r\n    INTO order_original_price, order_items_subtotal\r\n    FROM sales_order_items \r\n    WHERE order_id = COALESCE(NEW.order_id, OLD.order_id);\r\n    \r\n    order_discount_amount := order_original_price - order_items_subtotal;\r\n    \r\n    -- Update sales_orders - PRESERVE final_price if it was set by UI (includes tax + freight)\r\n    -- Only update final_price if it's currently 0 or matches items subtotal (meaning it wasn't set by UI)\r\n    UPDATE sales_orders SET\r\n        original_price = order_original_price,\r\n        discount_amount = order_discount_amount,\r\n        -- CRITICAL: Only update final_price if it appears to be unset or just item total\r\n        final_price = CASE \r\n            WHEN current_final_price = 0 OR current_final_price = order_items_subtotal THEN \r\n                order_items_subtotal + current_freight + current_tax_amount\r\n            ELSE \r\n                current_final_price  -- PRESERVE UI-calculated final_price\r\n        END,\r\n        -- Always preserve these UI-calculated fields\r\n        freight_charges = current_freight,\r\n        tax_percentage = current_tax_percentage,\r\n        tax_amount = current_tax_amount,\r\n        grand_total = CASE \r\n            WHEN current_grand_total = 0 THEN \r\n                order_items_subtotal + current_freight + current_tax_amount\r\n            ELSE \r\n                current_grand_total  -- PRESERVE UI-calculated grand_total\r\n        END,\r\n        updated_at = NOW()\r\n    WHERE id = COALESCE(NEW.order_id, OLD.order_id);\r\n    \r\n    RETURN COALESCE(NEW, OLD);\r\nEND;\r\n$function$\n"
  },
