  {
    "trigger_schema": "public",
    "table_name": "vendor_payment_history",
    "trigger_name": "trg_vendor_payments_audit",
    "trigger_definition": "CREATE TRIGGER trg_vendor_payments_audit BEFORE INSERT ON vendor_payment_history FOR EACH ROW EXECUTE FUNCTION trigger_audit_financial_transaction()",
    "enabled_flag": "O",
    "function_oid": 132003,
    "function_name": "trigger_audit_financial_transaction",
    "function_owner": "postgres",
    "function_definition": "CREATE OR REPLACE FUNCTION public.trigger_audit_financial_transaction()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nBEGIN\r\n    -- This can be extended to log to an audit table\r\n    -- For now, just ensure created_at is set\r\n    IF TG_OP = 'INSERT' THEN\r\n        IF NEW.created_at IS NULL THEN\r\n            NEW.created_at = CURRENT_TIMESTAMP;\r\n        END IF;\r\n        RETURN NEW;\r\n    END IF;\r\n    \r\n    RETURN NEW;\r\nEND;\r\n$function$\n"
  },
  {
    "trigger_schema": "public",
    "table_name": "vendor_payment_history",
    "trigger_name": "trg_vendor_payments_create_journal",
    "trigger_definition": "CREATE TRIGGER trg_vendor_payments_create_journal AFTER INSERT ON vendor_payment_history FOR EACH ROW EXECUTE FUNCTION trigger_create_vendor_payment_journal_entry()",
    "enabled_flag": "O",
    "function_oid": 132139,
    "function_name": "trigger_create_vendor_payment_journal_entry",
    "function_owner": "postgres",
    "function_definition": "CREATE OR REPLACE FUNCTION public.trigger_create_vendor_payment_journal_entry()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nDECLARE\r\n    cash_account_id UUID;\r\n    ap_account_id UUID;\r\n    journal_id UUID;\r\n    supplier_name TEXT;\r\n    payment_amount DECIMAL;\r\nBEGIN\r\n    -- Determine payment amount (handle different column names)\r\n    payment_amount := COALESCE(NEW.amount_paid, NEW.amount, 0);\r\n    \r\n    -- Skip if amount is null or zero\r\n    IF payment_amount IS NULL OR payment_amount = 0 THEN\r\n        RETURN NEW;\r\n    END IF;\r\n    \r\n    -- Skip if payment is not completed (if status field exists)\r\n    IF NEW.status IS NOT NULL AND NEW.status != 'completed' THEN\r\n        RETURN NEW;\r\n    END IF;\r\n    \r\n    -- Get account IDs with flexible lookup\r\n    SELECT id INTO cash_account_id FROM chart_of_accounts WHERE account_code IN ('1001', '1010') ORDER BY account_code LIMIT 1;\r\n    SELECT id INTO ap_account_id FROM chart_of_accounts WHERE account_code IN ('2010', '2000') ORDER BY account_code LIMIT 1;\r\n    \r\n    -- Skip if accounts not found but log warning\r\n    IF cash_account_id IS NULL OR ap_account_id IS NULL THEN\r\n        RAISE WARNING 'Required accounts not found for vendor payment journal entry: Cash=%, AP=%. Available codes: %', \r\n            cash_account_id, ap_account_id,\r\n            (SELECT array_agg(account_code) FROM chart_of_accounts WHERE account_code IN ('1001','1010','2010','2000'));\r\n        RETURN NEW;\r\n    END IF;\r\n    \r\n    -- Get supplier name\r\n    IF NEW.supplier_id IS NOT NULL THEN\r\n        SELECT name INTO supplier_name FROM suppliers WHERE id = NEW.supplier_id;\r\n    END IF;\r\n    \r\n    -- Create journal entry for vendor payment\r\n    journal_id := create_journal_entry(\r\n        COALESCE(NEW.payment_date, CURRENT_DATE),\r\n        'Payment to ' || COALESCE(supplier_name, 'Supplier'),\r\n        'VP-' || NEW.id::text\r\n    );\r\n    \r\n    -- Debit Accounts Payable (decrease liability)\r\n    PERFORM add_journal_entry_line(journal_id, ap_account_id, payment_amount, 0, 'Payment to supplier');\r\n    \r\n    -- Credit Cash (decrease asset)\r\n    PERFORM add_journal_entry_line(journal_id, cash_account_id, 0, payment_amount, 'Cash paid');\r\n    \r\n    -- Post the journal entry\r\n    PERFORM post_journal_entry(journal_id);\r\n    \r\n    RETURN NEW;\r\nEXCEPTION\r\n    WHEN OTHERS THEN\r\n        -- Log error and continue without failing the payment\r\n        RAISE WARNING 'Failed to create journal entry for vendor payment %: %', NEW.id, SQLERRM;\r\n        RETURN NEW;\r\nEND;\r\n$function$\n"
  },
  {
    "trigger_schema": "public",
    "table_name": "vendor_payment_history",
    "trigger_name": "trg_vendor_payments_update_po_status",
    "trigger_definition": "CREATE TRIGGER trg_vendor_payments_update_po_status AFTER INSERT OR DELETE OR UPDATE ON vendor_payment_history FOR EACH ROW EXECUTE FUNCTION trigger_update_po_payment_status()",
    "enabled_flag": "O",
    "function_oid": 131988,
    "function_name": "trigger_update_po_payment_status",
    "function_owner": "postgres",
    "function_definition": "CREATE OR REPLACE FUNCTION public.trigger_update_po_payment_status()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nBEGIN\r\n    -- Handle different trigger events\r\n    IF TG_OP = 'INSERT' OR TG_OP = 'UPDATE' THEN\r\n        -- Update for new/updated vendor payment\r\n        IF NEW.purchase_order_id IS NOT NULL THEN\r\n            PERFORM update_purchase_order_payment_status(NEW.purchase_order_id);\r\n        END IF;\r\n        RETURN NEW;\r\n    ELSIF TG_OP = 'DELETE' THEN\r\n        -- Update for deleted vendor payment\r\n        IF OLD.purchase_order_id IS NOT NULL THEN\r\n            PERFORM update_purchase_order_payment_status(OLD.purchase_order_id);\r\n        END IF;\r\n        RETURN OLD;\r\n    END IF;\r\n    \r\n    RETURN NULL;\r\nEND;\r\n$function$\n"
  },
  {
    "trigger_schema": "public",
    "table_name": "vendor_payment_history",
    "trigger_name": "trigger_update_po_payment_status",
    "trigger_definition": "CREATE TRIGGER trigger_update_po_payment_status AFTER INSERT OR DELETE OR UPDATE ON vendor_payment_history FOR EACH ROW EXECUTE FUNCTION update_purchase_order_payment_status()",
    "enabled_flag": "O",
    "function_oid": 76650,
    "function_name": "update_purchase_order_payment_status",
    "function_owner": "postgres",
    "function_definition": "CREATE OR REPLACE FUNCTION public.update_purchase_order_payment_status()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nBEGIN\r\n    UPDATE purchase_orders \r\n    SET payment_status = CASE \r\n        WHEN paid_amount = 0 THEN 'unpaid'\r\n        WHEN paid_amount >= total THEN 'paid'\r\n        WHEN paid_amount > 0 AND paid_amount < total THEN 'partially_paid'\r\n        ELSE payment_status\r\n    END\r\n    WHERE id = COALESCE(NEW.purchase_order_id, OLD.purchase_order_id);\r\n\r\n    RETURN COALESCE(NEW, OLD);\r\nEND;\r\n$function$\n"
  },
  {
    "trigger_schema": "public",
    "table_name": "vendor_payment_history",
    "trigger_name": "trigger_update_vendor_bill_status",
    "trigger_definition": "CREATE TRIGGER trigger_update_vendor_bill_status AFTER INSERT OR DELETE OR UPDATE ON vendor_payment_history FOR EACH ROW EXECUTE FUNCTION update_vendor_bill_status()",
    "enabled_flag": "O",
    "function_oid": 76648,
    "function_name": "update_vendor_bill_status",
    "function_owner": "postgres",
    "function_definition": "CREATE OR REPLACE FUNCTION public.update_vendor_bill_status()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nBEGIN\r\n    UPDATE vendor_bills \r\n    SET status = CASE \r\n        WHEN paid_amount = 0 THEN 'pending'\r\n        WHEN paid_amount >= total_amount THEN 'paid'\r\n        WHEN paid_amount > 0 AND paid_amount < total_amount THEN 'partial'\r\n        ELSE status\r\n    END,\r\n    updated_at = now()\r\n    WHERE id = COALESCE(NEW.vendor_bill_id, OLD.vendor_bill_id);\r\n\r\n    RETURN COALESCE(NEW, OLD);\r\nEND;\r\n$function$\n"
  },
  {
    "trigger_schema": "public",
